import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './Feed.css';

function TwitterFeed() {
  const [tweets, setTweets] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchTweets = async () => {
      try {
        const bearerToken = process.env.REACT_APP_TWITTER_BEARER_TOKEN;
        if (!bearerToken) {
          throw new Error('Twitter Bearer Token not found');
        }

        // First, get user ID for '_jmhj'
        const userResponse = await axios.get('https://api.twitter.com/2/users/by/username/_jmhj', {
          headers: {
            'Authorization': `Bearer ${bearerToken}`
          }
        });

        const userId = userResponse.data.data.id;

        // Then, get tweets
        const tweetsResponse = await axios.get(`https://api.twitter.com/2/users/${userId}/tweets`, {
          headers: {
            'Authorization': `Bearer ${bearerToken}`
          },
          params: {
            max_results: 5,
            'tweet.fields': 'created_at,text'
          }
        });

        setTweets(tweetsResponse.data.data || []);
      } catch (err) {
        setError(err.message);
      } finally {
        setLoading(false);
      }
    };

    fetchTweets();
  }, []);

  if (loading) {
    return (
      <div className="feed-card">
        <h3>X (Twitter) Posts</h3>
        <p>Loading latest posts...</p>
      </div>
    );
  }

  if (error) {
    return (
      <div className="feed-card">
        <h3>X (Twitter) Posts</h3>
        <p>Error: {error}</p>
      </div>
    );
  }

  return (
    <div className="feed-card">
      <h3>X (Twitter) Posts</h3>
      {tweets.length === 0 ? (
        <p>No posts available.</p>
      ) : (
        <ul>
          {tweets.map((tweet) => (
            <li key={tweet.id} style={{ marginBottom: '10px' }}>
              <p>{tweet.text}</p>
              <small>{new Date(tweet.created_at).toLocaleString()}</small>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
}

export default TwitterFeed;
