import React, { useState } from 'react';
import TwitterFeed from './TwitterFeed';
import MALFeed from './MALFeed';
import GoodreadsFeed from './GoodreadsFeed';
import SteamFeed from './SteamFeed';
import './FeedSection.css';

const feeds = [
  { component: <TwitterFeed />, name: 'X Posts', wrapper: 'twitter-wrapper' },
  { component: <MALFeed />, name: 'MAL Updates', wrapper: 'mal-wrapper' },
  { component: <GoodreadsFeed />, name: 'Goodreads Reviews', wrapper: 'goodreads-wrapper' },
  { component: <SteamFeed />, name: 'Steam Games', wrapper: 'steam-wrapper' },
];

function FeedSection() {
  const [currentFeed, setCurrentFeed] = useState(0);

  const nextFeed = () => {
    setCurrentFeed((prev) => (prev + 1) % feeds.length);
  };

  const prevFeed = () => {
    setCurrentFeed((prev) => (prev - 1 + feeds.length) % feeds.length);
  };

  return (
    <section id="feeds" className="feed-section">
      <h2>Live Feeds</h2>
      <div className="feed-carousel">
        <button className="carousel-arrow left-arrow" onClick={prevFeed}>&lt;</button>
        <div className="feed-container">
          <div key={currentFeed} className={`feed-wrapper ${feeds[currentFeed].wrapper}`}>
            {feeds[currentFeed].component}
          </div>
        </div>
        <button className="carousel-arrow right-arrow" onClick={nextFeed}>&gt;</button>
      </div>
      <div className="feed-indicators">
        {feeds.map((feed, index) => (
          <button
            key={index}
            className={`indicator ${index === currentFeed ? 'active' : ''}`}
            onClick={() => setCurrentFeed(index)}
          >
            {feed.name}
          </button>
        ))}
      </div>
    </section>
  );
}

export default FeedSection;
