import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './Feed.css';

function TwitterFeed() {
  const [tweets, setTweets] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchTweets = async () => {
      try {
        const response = await axios.get('http://localhost:3001/api/twitter?username=_jmhj');
        setTweets(response.data.tweets || []);
      } catch (err) {
        setError(err.message);
      } finally {
        setLoading(false);
      }
    };

    fetchTweets();

    // Poll every 5 minutes
    const interval = setInterval(fetchTweets, 5 * 60 * 1000);

    return () => clearInterval(interval);
  }, []);

  if (loading) {
    return (
      <div className="feed-card">
        <h3>X (Twitter) Posts</h3>
        <p>Loading latest posts...</p>
      </div>
    );
  }

  if (error) {
    return (
      <div className="feed-card">
        <h3>X (Twitter) Posts</h3>
        <p>Error: {error}</p>
      </div>
    );
  }

  return (
    <div className="feed-card">
      <h3>X (Twitter) Posts</h3>
      {tweets.length === 0 ? (
        <p>No posts available.</p>
      ) : (
        <ul>
          {tweets.map((tweet) => (
            <li key={tweet.id} style={{ marginBottom: '10px' }}>
              <p>{tweet.text}</p>
              <small>{new Date(tweet.created_at).toLocaleString()}</small>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
}

export default TwitterFeed;
