import React, { useState } from 'react';
import Header from './Header';

const projects = [
  {
    title: 'Personal Expense Tracker',
    description: 'A Streamlit app for tracking personal expenses.',
    link: 'https://personal-expense-tracker-jmhj.streamlit.app/',
  },
  {
    title: 'PowerShell Data Automation Toolkit',
    description: 'A comprehensive PowerShell toolkit for Windows system administration, featuring data export/analysis, GUI interface, and automated scheduling.',
    link: 'https://github.com/jmhj-0/data-automation-toolkit',
  },
  {
    title: 'SQL Query Performance Analyzer',
    description: 'A web application for analyzing SQL query performance, providing optimization suggestions across multiple database dialects including MySQL, PostgreSQL, and SQL Server.',
    link: 'https://web-production-03c63.up.railway.app/',
  },
];

function ProjectsPage() {
  const [currentIndex, setCurrentIndex] = useState(0);

  const nextSlide = () => {
    setCurrentIndex((prevIndex) => (prevIndex + 1) % projects.length);
  };

  const prevSlide = () => {
    setCurrentIndex((prevIndex) => (prevIndex - 1 + projects.length) % projects.length);
  };

  const goToSlide = (index) => {
    setCurrentIndex(index);
  };

  return (
    <div className="App">
      <div className="bg-effects">
        <div className="bg-shape"></div>
        <div className="bg-shape"></div>
        <div className="bg-shape"></div>
        <div className="bg-shape"></div>
      </div>
      <Header />
      <main>
        <h2>My Projects</h2>
        <div className="projects-carousel">
          <button className="carousel-btn prev" onClick={prevSlide} aria-label="Previous project">
            ‹
          </button>
          <div className="carousel-container">
            <div
              className="carousel-track"
              style={{ transform: `translateX(-${currentIndex * 100}%)` }}
            >
              {projects.map((project, index) => (
                <div key={index} className="project-card">
                  <h3>{project.title}</h3>
                  <p>{project.description}</p>
                  <a href={project.link} target="_blank" rel="noopener noreferrer" className="project-link">
                    View Project
                  </a>
                </div>
              ))}
            </div>
          </div>
          <button className="carousel-btn next" onClick={nextSlide} aria-label="Next project">
            ›
          </button>
          <div className="carousel-dots">
            {projects.map((_, index) => (
              <button
                key={index}
                className={`dot ${index === currentIndex ? 'active' : ''}`}
                onClick={() => goToSlide(index)}
                aria-label={`Go to project ${index + 1}`}
              />
            ))}
          </div>
        </div>
      </main>
    </div>
  );
}

export default ProjectsPage;
