import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './Feed.css';

function SteamFeed() {
  const [games, setGames] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchGames = async () => {
      try {
        const response = await axios.get('http://localhost:3001/api/steam?steamid=jmhj');
        setGames(response.data.games || []);
      } catch (err) {
        setError(err.message);
      } finally {
        setLoading(false);
      }
    };

    fetchGames();
  }, []);

  if (loading) {
    return (
      <div className="feed-card">
        <h3>Steam Recently Played</h3>
        <p>Loading game activity...</p>
      </div>
    );
  }

  if (error) {
    return (
      <div className="feed-card">
        <h3>Steam Recently Played</h3>
        <p>Error: {error}</p>
      </div>
    );
  }

  return (
    <div className="feed-card">
      <h3>Steam Recently Played</h3>
      {games.length === 0 ? (
        <p>No recent games.</p>
      ) : (
        <ul>
          {games.map((game, index) => (
            <li key={index} style={{ marginBottom: '10px' }}>
              {game.img_icon_url && <img src={game.img_icon_url} alt={game.name} style={{ width: '32px', height: '32px', marginRight: '10px' }} />}
              <strong>{game.name}</strong>
              <br />
              <small>Played 2 weeks: {Math.floor(game.playtime_2weeks / 60)}h {game.playtime_2weeks % 60}m | Total: {Math.floor(game.playtime_forever / 60)}h {game.playtime_forever % 60}m</small>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
}

export default SteamFeed;
