import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './Feed.css';

function MALFeed() {
  const [anime, setAnime] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchAnime = async () => {
      try {
        const response = await axios.get('http://localhost:3001/api/mal?username=jmhj');
        setAnime(response.data.anime || []);
      } catch (err) {
        setError(err.message);
      } finally {
        setLoading(false);
      }
    };

    fetchAnime();

    // Poll every 5 minutes
    const interval = setInterval(fetchAnime, 5 * 60 * 1000);

    return () => clearInterval(interval);
  }, []);

  if (loading) {
    return (
      <div className="feed-card">
        <h3>MyAnimeList Updates</h3>
        <p>Loading anime list updates...</p>
      </div>
    );
  }

  if (error) {
    return (
      <div className="feed-card">
        <h3>MyAnimeList Updates</h3>
        <p>Error: {error}</p>
      </div>
    );
  }

  return (
    <div className="feed-card">
      <h3>MyAnimeList Updates</h3>
      {anime.length === 0 ? (
        <p>No recent updates.</p>
      ) : (
        <ul>
          {anime.map((item, index) => (
            <li key={index} style={{ marginBottom: '10px' }}>
              <img src={item.image_url} alt={item.title} style={{ width: '50px', height: '70px', marginRight: '10px' }} />
              <a href={item.url} target="_blank" rel="noopener noreferrer">{item.title}</a>
              <br />
              <small>Status: {item.status} | Score: {item.score}/10 | Updated: {new Date(item.updated_at).toLocaleDateString()}</small>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
}

export default MALFeed;
