import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './Feed.css';

function GoodreadsFeed() {
  const [reviews, setReviews] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchReviews = async () => {
      try {
        const response = await axios.get('http://localhost:3001/api/goodreads?user_id=145364540');
        setReviews(response.data.reviews || []);
      } catch (err) {
        setError(err.message);
      } finally {
        setLoading(false);
      }
    };

    fetchReviews();

    // Poll every 5 minutes
    const interval = setInterval(fetchReviews, 5 * 60 * 1000);

    return () => clearInterval(interval);
  }, []);

  if (loading) {
    return (
      <div className="feed-card">
        <h3>Goodreads Updates</h3>
        <p>Loading book reviews...</p>
      </div>
    );
  }

  if (error) {
    return (
      <div className="feed-card">
        <h3>Goodreads Updates</h3>
        <p>Error: {error}</p>
      </div>
    );
  }

  return (
    <div className="feed-card">
      <h3>Goodreads Updates</h3>
      {reviews.length === 0 ? (
        <p>No recent reviews.</p>
      ) : (
        <ul>
          {reviews.map((review, index) => (
            <li key={index} style={{ marginBottom: '10px' }}>
              {review.book_image && <img src={review.book_image} alt={review.title} style={{ width: '50px', height: '70px', marginRight: '10px' }} />}
              <a href={review.book_url} target="_blank" rel="noopener noreferrer">{review.title}</a> by {review.author}
              <br />
              <small>Rating: {review.rating}/5 | Reviewed: {new Date(review.review_date).toLocaleDateString()}</small>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
}

export default GoodreadsFeed;
