import React, { useState } from 'react';
import TwitterFeed from './TwitterFeed';
import MALFeed from './MALFeed';
import GoodreadsFeed from './GoodreadsFeed';
import SteamFeed from './SteamFeed';
import './FeedSection.css';

const feedTypes = [
  { id: 'twitter', name: 'X Posts', component: TwitterFeed, wrapper: 'twitter-wrapper' },
  { id: 'mal', name: 'MAL Updates', component: MALFeed, wrapper: 'mal-wrapper' },
  { id: 'goodreads', name: 'Goodreads Reviews', component: GoodreadsFeed, wrapper: 'goodreads-wrapper' },
  { id: 'steam', name: 'Steam Games', component: SteamFeed, wrapper: 'steam-wrapper' },
];

function FeedSection() {
  const [visibleFeeds, setVisibleFeeds] = useState(feedTypes.map(f => f.id));
  const [collapsedFeeds, setCollapsedFeeds] = useState(new Set());

  const toggleFeedVisibility = (feedId) => {
    setVisibleFeeds(prev =>
      prev.includes(feedId)
        ? prev.filter(id => id !== feedId)
        : [...prev, feedId]
    );
  };

  const toggleFeedCollapse = (feedId) => {
    setCollapsedFeeds(prev => {
      const newSet = new Set(prev);
      if (newSet.has(feedId)) {
        newSet.delete(feedId);
      } else {
        newSet.add(feedId);
      }
      return newSet;
    });
  };

  const showAllFeeds = () => setVisibleFeeds(feedTypes.map(f => f.id));
  const hideAllFeeds = () => setVisibleFeeds([]);

  return (
    <section id="feeds" className="feed-section">
      <h2>Live Feeds</h2>
      <div className="feed-controls">
        <div className="filter-buttons">
          <button onClick={showAllFeeds} className="control-btn">Show All</button>
          <button onClick={hideAllFeeds} className="control-btn">Hide All</button>
          {feedTypes.map(feed => (
            <button
              key={feed.id}
              onClick={() => toggleFeedVisibility(feed.id)}
              className={`control-btn ${visibleFeeds.includes(feed.id) ? 'active' : ''}`}
            >
              {feed.name}
            </button>
          ))}
        </div>
      </div>
      <div className="feeds-grid">
        {feedTypes
          .filter(feed => visibleFeeds.includes(feed.id))
          .map(feed => {
            const FeedComponent = feed.component;
            return (
              <div key={feed.id} className={`feed-wrapper ${feed.wrapper} ${collapsedFeeds.has(feed.id) ? 'collapsed' : ''}`}>
                <div className="feed-header">
                  <h3>{feed.name}</h3>
                  <button
                    className="collapse-btn"
                    onClick={() => toggleFeedCollapse(feed.id)}
                  >
                    {collapsedFeeds.has(feed.id) ? '+' : '-'}
                  </button>
                </div>
                {!collapsedFeeds.has(feed.id) && <FeedComponent />}
              </div>
            );
          })}
      </div>
    </section>
  );
}

export default FeedSection;
